import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { PrizeService } from '../../services/prize.service';

@Component({
  selector: 'app-prizes',
  templateUrl: './prizes.component.html',
  styleUrls: ['./prizes.component.scss']
})
export class PrizesComponent implements OnInit {
  @Input() prizes: { name: string }[] = [];
  @Output() prizesChange = new EventEmitter<{ name: string }[]>();
  newPrize: string = "";
  showModal: boolean = false;

  constructor(private _prizeServices: PrizeService) { }

  ngOnInit(): void {
    this.loadPrizes();
  }

  loadPrizes(): void {
    this.prizes = this._prizeServices.getPrizes();
  }
  openModal(): void {
    this.showModal = true;
  }

  closeModal(): void {
    this.showModal = false;
    this.newPrize = '';
  }

  addPrize(): void {
    if (this.newPrize.trim()) {
      this.prizes.push({ name: this.newPrize });
      this._prizeServices.savePrize(this.prizes);
      this.newPrize = '';
      // this.closeModal();
    }
  }

  removePrize(prize: { name: string }) {
    const index = this.prizes.indexOf(prize);
    if (index !== -1) {
      this.prizes.splice(index, 1);
      this._prizeServices.savePrize(this.prizes);
    }
  }




}
<div>
  <h2 class="text-lg font-semibold mb-4">Premios</h2>
  <div *ngFor="let prize of prizes; let i = index" class="flex mb-2">
    <input
      type="text"
      [(ngModel)]="prizes[i].name"
      placeholder="Premio"
      class="border border-gray-200 rounded-l-lg px-3 py-2 focus:outline-none focus:ring focus:border-blue-500 flex-grow"
    />
    <button
      class="bg-red-500 text-white px-2 py-2 rounded-r-lg hover:bg-red-600 focus:outline-none focus:ring focus:border-blue-500"
      (click)="removePrize(i)"
    >
      <svg
        xmlns="http://www.w3.org/2000/svg"
        class="h-5 w-5 text-white"
        viewBox="0 0 20 20"
        fill="currentColor"
      >
        <path
          fill-rule="evenodd"
          d="M5 5a1 1 0 011-1h8a1 1 0 110 2H6a1 1 0 01-1-1zM9 15a1 1 0 100 2 1 1 0 000-2zm5-5a1 1 0 111 1 1 1 0 01-1-1 1 1 0 010-1zM6 9a1 1 0 100 2h8a1 1 0 110 2H6a1 1 0 100 2h8a3 3 0 003-3V6a3 3 0 00-3-3H6a3 3 0 00-3 3v6z"
          clip-rule="evenodd"
        />
      </svg>
    </button>
  </div>
  <button
    class="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 focus:outline-none focus:ring focus:border-blue-500"
    (click)="addPrize()"
  >
    Agregar Premio
  </button>
</div>
