import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-prizes',
  templateUrl: './prizes.component.html',
  styleUrls: ['./prizes.component.scss']
})
export class PrizesComponent implements OnInit {
  @Input() prizes: { name: string }[] = [];
  @Output() prizesChange = new EventEmitter<{ name: string }[]>();
  newPrize: string = "";
  showModal: boolean = false;

  constructor() { }

  ngOnInit(): void {
  }

  openModal(): void {
    this.showModal = true;
  }

  closeModal(): void {
    this.showModal = false;
    this.newPrize = '';
  }

  addPrize(): void {
    if (this.newPrize.trim()) {
      this.prizes.push({ name: this.newPrize });
      this.newPrize = '';
      this.closeModal();
    }
  }


}
<div>
  <button
    class="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring focus:border-blue-500"
    (click)="openModal()"
  >
    Agregar Premio
  </button>

  <!-- Modal para agregar premios -->
  <div
    *ngIf="showModal"
    class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50"
  >
    <div class="bg-white p-8 rounded-lg shadow-lg w-96">
      <h2 class="text-lg font-semibold mb-4">Agregar Premio</h2>
      <input
        type="text"
        [(ngModel)]="newPrize"
        placeholder="Nuevo Premio"
        class="border border-gray-200 rounded-lg px-3 py-2 mb-2 focus:outline-none focus:ring focus:border-blue-500"
      />
      <div class="flex justify-between">
        <button
          class="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring focus:border-blue-500"
          (click)="addPrize()"
        >
          Agregar
        </button>
        <button
          class="bg-gray-300 text-gray-700 px-6 py-2 rounded-lg hover:bg-gray-400 focus:outline-none focus:ring focus:border-blue-500"
          (click)="closeModal()"
        >
          Cancelar
        </button>
      </div>
    </div>
  </div>
</div>
