import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { PrizeService } from '../../services/prize.service';

@Component({
  selector: 'app-prizes',
  templateUrl: './prizes.component.html',
  styleUrls: ['./prizes.component.scss']
})
export class PrizesComponent implements OnInit {
  @Input() prizes: { name: string }[] = [];
  @Output() prizesChange = new EventEmitter<{ name: string }[]>();
  newPrize: string = "";
  showModal: boolean = false;

  constructor(private _prizeServices: PrizeService) { }

  ngOnInit(): void {
    this.loadPrizes();
  }

  loadPrizes(): void {
    this.prizes = this._prizeServices.getPrizes();
  }
  openModal(): void {
    this.showModal = true;
  }

  closeModal(): void {
    this.showModal = false;
    this.newPrize = '';
  }

  addPrize(): void {
    if (this.newPrize.trim()) {
      this.prizes.push({ name: this.newPrize });
      this._prizeServices.savePrize(this.prizes);
      this.newPrize = '';
      // this.closeModal();
    }
  }

  removePrize(prize: { name: string }): void {
    const index = this.prizes.indexOf(prize);
    if (index !== -1) {
      this.prizes.splice(index, 1);
      this._prizeServices.removeSelectedPrize(index);
      this.prizes = this._prizeServices.getPrizes();
      this._prizeServices.savePrize(this.prizes);
    }
  }

}
<div>
  <!-- Botón para abrir el modal de premios -->
  <button
    class="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring focus:border-blue-500"
    (click)="openModal()"
  >
    Agregar Premio
  </button>

  <!-- Modal para agregar premios -->
  <div
    *ngIf="showModal"
    class="fixed inset-0 overflow-y-auto flex justify-center items-center bg-gray-800 bg-opacity-50 z-50"
  >
    <div class="bg-white p-8 rounded-lg shadow-lg w-96">
      <h2 class="text-lg font-semibold mb-4">Agregar Premio</h2>

      <!-- Formulario para agregar premio -->
      <div class="flex mb-4">
        <input
          type="text"
          [(ngModel)]="newPrize"
          placeholder="Nuevo premio"
          class="border border-gray-200 rounded-l-lg px-3 py-2 focus:outline-none focus:ring focus:border-blue-500 flex-grow"
        />
        <button
          class="bg-green-500 text-white px-2 py-2 rounded-r-lg hover:bg-green-600 focus:outline-none focus:ring focus:border-blue-500"
          (click)="addPrize()"
        >
          Agregar
        </button>
      </div>

      <!-- Tabla que muestra los premios agregados -->
      <div
        class="max-h-48 overflow-y-auto border border-gray-200 rounded-lg mb-6 relative scrollbar-thin scrollbar-thumb-blue-500 scrollbar-track-gray-100"
      >
        <table class="w-full">
          <thead>
            <tr>
              <th class="px-4 py-2">Premio</th>
              <th class="px-4 py-2"></th>
            </tr>
          </thead>
          <tbody>
            <ng-container *ngIf="prizes.length > 0; else noPrizes">
              <tr *ngFor="let prize of prizes">
                <td class="border px-4 py-2">{{ prize.name }}</td>
                <td class="border px-4 py-2">
                  <button
                    class="text-red-500 hover:text-red-700 focus:outline-none focus:text-red-700 transition duration-200"
                    (click)="removePrize(prize)"
                  >
                    Eliminar
                  </button>
                </td>
              </tr>
            </ng-container>
            <ng-template #noPrizes>
              <tr>
                <td colspan="2" class="px-4 py-2 text-gray-400">
                  No hay premios agregados
                </td>
              </tr>
            </ng-template>
          </tbody>
        </table>
      </div>

      <!-- Botón para cerrar el modal -->
      <div class="text-right">
        <button
          class="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 focus:outline-none focus:ring focus:border-blue-500"
          (click)="closeModal()"
        >
          Cerrar
        </button>
      </div>
    </div>
  </div>
</div>
