import { Component, OnInit } from '@angular/core';
import { PaticipantesService } from '../../services/paticipantes.service';
import { BaseApiResponse } from 'src/app/shared/models/base-api-response.interface';

@Component({
  selector: 'app-sorteo',
  templateUrl: './sorteo.component.html',
  styleUrls: ['./sorteo.component.scss']
})
export class SorteoComponent {

  participants: string[] = [];

  constructor(private _participanteServices: PaticipantesService) {
    this.participants = this._participanteServices.getData('participante') || [];
  }


  addParticipant(name: string) {
    this.participants.push(name);
    this._participanteServices.saveData('participante', this.participants)
  }

  deleteParticipant(name: string) {
    const index = this.participants.indexOf(name);
    if (index !== -1) {
      this.participants.splice(index, 1);
      this._participanteServices.saveData('participante', this.participants);
    }
  }

}
<!-- app.component.html -->

<h1>Participantes del Sorteo</h1>

<ul>
  <li *ngFor="let participant of participants">
    {{ participant }}
    <button (click)="deleteParticipant(participant)">Borrar</button>
  </li>
</ul>

<input
  type="text"
  #participantName
  placeholder="Nombre del participante"
  (keyup.enter)="addParticipant(participantName)"
/>
<button (click)="addParticipant(participantName)">Agregar Participante</button>
