import { Component, OnInit } from '@angular/core';
import { PaticipantesService } from '../../services/paticipantes.service';

@Component({
  selector: 'app-sorteo',
  templateUrl: './sorteo.component.html',
  styleUrls: ['./sorteo.component.scss']
})
export class SorteoComponent {

  participants: string[] = [];

  constructor(private _participanteServices: PaticipantesService) {
    this.participants = this._participanteServices.getData('participante') || [];
  }


  addParticipant(participantInput: HTMLInputElement): void {
    const name = participantInput.value.trim();
    if (name) {
      this.participants.push(name);
      this._participanteServices.saveData('participants', this.participants);
      participantInput.value = ''; // Limpiar el input
    }
  }

  deleteParticipant(name: string) {
    const index = this.participants.indexOf(name);
    if (index !== -1) {
      this.participants.splice(index, 1);
      this._participanteServices.saveData('participante', this.participants);
    }
  }

}
<div class="flex justify-center items-center h-screen bg-gray-100">
  <div class="bg-white p-8 rounded-lg shadow-md w-96">
    <h1 class="text-2xl font-bold mb-4">Participantes del Sorteo</h1>

    <div class="max-h-60 overflow-y-auto mb-4">
      <ul>
        <li
          *ngFor="let participant of participants"
          class="flex justify-between items-center py-2 border-b border-gray-200"
        >
          <span>{{ participant }}</span>
          <button class="text-red-500" (click)="deleteParticipant(participant)">
            Borrar
          </button>
        </li>
      </ul>
    </div>

    <div class="flex">
      <input
        type="text"
        #participantName
        placeholder="Nombre del participante"
        class="flex-grow border border-gray-200 rounded px-3 py-2 mr-2"
        (keyup.enter)="addParticipant(participantName)"
      />
      <button
        class="bg-blue-500 text-white px-4 py-2 rounded"
        (click)="addParticipant(participantName)"
      >
        Agregar
      </button>
    </div>
  </div>
</div>
