import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { PrizeService } from '../../services/prize.service';

@Component({
  selector: 'app-prizes',
  templateUrl: './prizes.component.html',
  styleUrls: ['./prizes.component.scss']
})
export class PrizesComponent implements OnInit {
  @Input() prizes: { name: string }[] = [];
  @Output() prizesChange = new EventEmitter<{ name: string }[]>();

  constructor(private _prizesServices: PrizeService) { }

  ngOnInit(): void {
    // Cargar premios desde el almacenamiento local al iniciar el componente
    const savedPrizes = this._prizesServices.getPrizes();
    if (savedPrizes) {
      this.prizes = savedPrizes;
    }
  }

  addPrize(): void {
    this.prizes.push({ name: '' });
    this.savePrizesToStorage();
    this.emitChanges();
  }

  removePrize(index: number): void {
    this.prizes.splice(index, 1);
    this.savePrizesToStorage();
    this.emitChanges();
  }

  private emitChanges(): void {
    if (this.prizesChange) {
      this.prizesChange.emit(this.prizes);
    }
  }

  private savePrizesToStorage(): void {
    this._prizesServices.savePrize(this.prizes);
  }
}
<div>
  <button
    class="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 focus:outline-none focus:ring focus:border-blue-500"
    (click)="addPrize()"
  >
    Agregar Premio
  </button>
  <div
    class="max-h-48 overflow-y-auto border border-gray-200 rounded-lg mb-6 relative scrollbar-thin scrollbar-thumb-blue-500 scrollbar-track-gray-100"
  >
    <table class="table-fixed w-full mt-4">
      <thead>
        <tr>
          <th class="px-4 py-2 w-3/4">Premio</th>
          <th class="px-4 py-2 w-1/4">Eliminar</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let prize of prizes; let i = index">
          <td class="border px-4 py-2">
            <input
              type="text"
              [(ngModel)]="prizes[i].name"
              class="w-full border-none bg-transparent"
            />
          </td>
          <td class="border px-4 py-2">
            <button class="text-red-500" (click)="removePrize(i)">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                class="h-6 w-6"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  stroke-linecap="round"
                  stroke-linejoin="round"
                  stroke-width="2"
                  d="M6 18L18 6M6 6l12 12"
                />
              </svg>
            </button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
